sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('sys_choice');
	noteGR.addEncodedQuery("nameLIKEcourse^elementSTARTSWITHlevel");
	noteGR.query();
	while(noteGR.next())
	{
		var noteObj = {};
		noteObj.level = noteGR.getDisplayValue('value');
		data.notes.push(noteObj);
	}  
  console.log(data.notes);
})();
