sp_widget.client_script:
api.controller=function() {
  /* widget controller */
	var c = this;
  c.divClicked = function(type,url,html){
		 console.log(type);
		if(type == "External URL"){
			window.open(url, '_blank');

		} else {
			alert("This is html, would you like to open in new tab?");
		}
	};


};

