sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	debugger;
	g_form.hideAllFieldMsgs('duration');
   if (isLoading || newValue === '') {
      return;
   }
   var  duration = g_form.getValue('duration');
   var time,days='NaN';
   if(duration.indexOf(' ')>0)
   {
	 days =  duration.split(' ')[0];
     time =  duration.split(' ')[1];
   }
   else time =  duration;
   var hour =  parseInt(time.split(':')[0]);
   var minute = parseInt(time.split(':')[1]);
   var seconds = parseInt(time.split(':')[2]);
   if( isNaN(days) || days<0 ||  hour < 0 || hour > 23 || minute <0 || minute >59 || seconds<0 || seconds >59)
   {
	g_form.setValue('duration','00 00:00:00');
	g_form.showFieldMsg('duration','Please enter Valid time ');
   } 


   //Type appropriate comment here, and begin script below

}
